import psutil, os, tracemalloc, time  # For memory and timing

###############################################################################
# 1) The Brusselator ODE
###############################################################################
def brusselator(t, Y, A=1.0, B=3.0):
    u, v = Y
    du = A - (B + 1) * u + u**2 * v
    dv = B * u - u**2 * v
    return np.array([du, dv])

